    def submit_clicked(self):
        item_name = self.add_item_input.toPlainText()
        item_price = self.add_price_input.value()
        if item_name == '':
            self.add_last_label.setText("Please Enter on the Item area!!")
            self.add_last_label.adjustSize()
        elif item_price == 0.00:
            self.add_last_label.setText("Price can not be 0.00!!")
            self.add_last_label.adjustSize()
        else:
            from time import sleep
            from MarCreate import Marcreate
            add = Marcreate(item_name,item_price)
            a = add.validity()
            
            self.add_last_label.setText("Please wait...")
            self.add_last_label.adjustSize()
            self.add_last_label.show()
            self.add_last_label.repaint()
            sleep(1.5)
            if a == False:
                self.add_last_label.setText(f"There is an Item named {item_name}\n(Please try with other or Edit the Exisitng)")
                self.add_last_label.adjustSize()
                self.add_last_label.show()
                self.add_last_label.repaint()
            else:
                add.savearea()
                self.add_last_label.setText("Sucessfully Saved Your Item!!")
                self.add_last_label.adjustSize()
                self.add_last_label.show()
                self.add_last_label.repaint()

