    item = ''
    price = ''
    def submit_clicked(self):
        item = self.edit_item_input.toPlainText()
        price = self.edit_price_input.value()
        if item == '':
            self.edit_sucess.setText("Please Enter on the Item area!!")
            self.edit_sucess.adjustSize()
        elif price == 0.00:
            self.edit_sucess.setText("Price can't be 0.00")
            self.edit_sucess.adjustSize()
        else:
            from time import sleep
            from MarEdit import MarEdit
            
            ed = MarEdit(item,price)
            ed_c = ed.checkfile()
            
            self.edit_sucess.setText("Please wait...")
            self.edit_sucess.adjustSize()
            self.edit_sucess.show()
            self.edit_sucess.repaint()
            sleep(1.5)

            if ed_c == False:
                self.edit_sucess.setText(f"Their is no item called {item}\n (Please try to create one!!)")
                self.edit_sucess.adjustSize()
                self.edit_sucess.show()
                self.edit_sucess.repaint()
            else:
                self.edit_sucess.setText("Sucessfully Edited your Item!!")
                self.edit_sucess.adjustSize()
                self.edit_sucess.show()
                self.edit_sucess.repaint()
                

