    item_name = ''
    def search_clicked(self):
        item_name = self.search_item_input.toPlainText()
        if item_name == '':
            self.search_item_price.setText("Please Enter on the Item area!!")
            self.search_item_price.adjustSize()
        else:
            from time import sleep
            from MarSearch import MarSearch
            sea = MarSearch(item_name)
            pr = sea.checkfile()
            
            self.search_item_price.setText("Please wait...")
            self.search_item_price.adjustSize()
            self.search_item_price.show()
            self.search_item_price.repaint()
            sleep(1.5)
            if pr == False:
                self.search_item_price.setText(f"There is no {item_name} kind of item(Please try again!)")
                self.search_item_price.adjustSize()
                self.search_item_price.show()
                self.search_item_price.repaint()
                
            else:
                self.search_item_price.setText(f"The price for {item_name} is {pr}")
                self.search_item_price.adjustSize()
                self.search_item_price.show()
                self.search_item_price.repaint()
                


